						<view class="price" @tap="toGoods(item)" v-if="item.dis_price!==undefined">
							<text style="font-size: 24rpx;">¥</text>{{ item.dis_price }}
							<text class="dis_price" v-if="item.dis_price!==undefined">{{ item.pro_price }}</text>
						</view>
						<view class="price" @tap="toGoods(item)" v-else>
							<text style="font-size: 24rpx;">¥</text>{{ item.pro_price }}
						</view>
						<view class="buy" @tap="tobuy(item)">
							<image src="../../static/img/tabBar/cart-on.png" mode="" class="cart"></image>
							<!-- <text class="cartnum" v-if="item.itemcount>0">{{item.itemcount}}</text> -->
						</view>
					</view>
				</view>
			</view>
		</view>
		<view class="line"></view>
		<view class="info-three">
			<view class="title">
				<text class="main">猜你喜欢</text>
			</view>
		</view>
		<view class="goods-list" style="padding-top: 0;">
			<view class="product-list">
				<view class="product" v-for="(item,index) in Promotion" :key="index">
					<image mode="widthFix" :src="item.pro_sn" @tap="toGoods(item)"></image>
					<view class="name title-one" @tap="toGoods(item)">{{ item.pro_name }}</view>
					<view class="info">
						<view class="price-info">
							<view class="price" v-if="item.dis_price!==undefined" @tap=" toGoods(item)">
								<text style="font-size: 24rpx;">¥</text>{{ item.dis_price }}
								<text class="dis_price" v-if="item.dis_price!==undefined">{{ item.pro_price }}</text>
							</view>
							<view class="price" v-else @tap=" toGoods(item)">
								<text style="font-size: 24rpx;">¥</text>{{ item.pro_price }}
							</view>
							<view class="buy" @tap="tobuy(item)">
								<image src="../../static/img/tabBar/cart-on.png" mode="" class="cart"></image>
								<!-- <text class="cartnum" v-if="item.itemcount>0">{{item.itemcount}}</text> -->
							</view>
						</view>

					</view>

				</view>
			</view>
			<view class="loading-text">{{ loadingText }}</view>
		</view>
		<modal :show="modalShow" @cancel="modalShow=false" :custom="true">
			<view class="popImg">
				<image :src="popup.img" mode="" @tap="modalShow=false" :style="{height:popHeight+'px'}"></image>
			</view>
		</modal>
		<tui-scroll-top :scrollTop="scrollTop"></tui-scroll-top>
	</view>
</template>

<script>
	import QQMapWX from '@/common/utils/qqmap-wx-jssdk.min.js'
	import modal from "@/components/modal/modal"
	import tuiIcon from "@/components/icon/icon"
	import md5 from "@/common/utils/md5.js"
	import tuiCountdown from "@/components/countdown/countdown"
	import {
		ajax,
		getSecret
	} from '@/common/ajax/ajax.js'
	export default {
		onShareAppMessage(res) {
			return {
				title: '熙美诚品',
				path: 'pages/index/index',
				success: (res) => {
					console.log(res);
				},
				fail: (res) => {
					console.log(res);
				}
			}
		},
		components: {
			modal,
			tuiIcon,
			tuiCountdown
		},
		data() {
			return {
				userProvider:'',
				scrollTop: 0,
				getheight: '',
				specialImgHeight: '',
				storeName: '总部旗舰店',
				address: '',
				city: '',
				id: '',
				secret: {},
				showHeader: true,
				afterHeaderOpacity: 1, //不透明度
				headerPosition: 'fixed',
				headerTop: null,
				statusTop: null,
				nVueTitle: null,
				currentSwiper: 0,
				// 轮播图片
				swiperList: [],
				// 活动图片
				actionBanner: [],
				// 分类菜单
				categoryList: [],
				// 限时抢购
				seckillList: [],
				// 砍价
				bartainlist: [],
				// 拼团
				assembleList: [],
				// 活动图片
				imgList: [{
						img: '../../static/img/01.png',
						key: '1'
					},
					{
						img: '../../static/img/02.png',
						key: '2'
					},
					{
						img: '../../static/img/03.gif',
						key: '3'
					}
				],
				seckillTime: 0,
				bargainList: [],
				Promotion: [],
				//猜你喜欢列表
				productList: [],
				// 活动
				specialList: [],
				loadingText: '到底了',
				amapPlugin: null,
				// 定位弹窗
				modalShow: false,
				storeItems: [{
					store_name: '总部旗舰店',
					desc: '由总部统一发货',
					store_code: '10001'
				}],
				// 门店编码
				store: '10001',
				hint: '熙美诚品',
				popup: {},
				// 向左滑动距离
				scrollLeft: 0,
				// 弹窗高度
				popHeight: 0,
				addShow: true,
				// 品牌
				traitList: [],
				traitListImg: [
					'/static/img/pinpai.png',
					'/static/img/baoyou.png',
					'/static/img/jisu.png'
				]
			};
		},
		onPageScroll(e) {
			this.scrollTop = e.scrollTop;
			//兼容iOS端下拉时顶部漂移
			this.headerPosition = e.scrollTop >= 0 ? "fixed" : "absolute";
			this.headerTop = e.scrollTop >= 0 ? null : 0;
			this.statusTop = e.scrollTop >= 0 ? null : -this.statusHeight + 'px';
		},
		//下拉刷新，需要自己在page.json文件中配置开启页面下拉刷新 "enablePullDownRefresh": true
		onPullDownRefresh() {
			this.getgoods();
			this.getassembleList();
			this.getBargin();
			setTimeout(function() {
				uni.stopPullDownRefresh();
			}, 1000);
		},

		onLoad() {
			this.getbig();
			this.getgoods();
			this.getassembleList();
			this.getLocation();
			this.getBargin();
			this.userProvider=this.$xm.userProvider();
			if(this.userProvider!='weixin'){
				uni.login({
					success: (res) => {
						console.log(res)
						if (res.code) {
							var code = res.code
							
							uni.getUserInfo({
								success: (res) => {
									console.log(res)
									let data = {
										encryptedData: res.encryptedData,
										iv: res.iv
									}
									this.$xm.post('/Index/getUnionid', data, (res) => {
										const user = uni.getStorageSync('user');
										user.unionid=res.unionid;
										uni.setStorage({
											key: 'user',
											data: user,
											success: (res) => {
												console.log(res);
											}
										});
									})
									uni.setStorage({
										key: 'userInfo',
										data: res.userInfo,
										success: (res) => {
											console.log(res);
										}
									});
				
								},
								fail: res => {
									console.log(res)
									// 获取失败的去引导用户授权 
								}
							})
				
						} else {}
					}
				})
			}
		},
		onShow() {
			console.log(uni.getStorageSync('store'));
			if (uni.getStorageSync('store')) {
				this.storeName = uni.getStorageSync('store').store_name;
				this.store = uni.getStorageSync('store').store_code
				this.getgoods();

			}

		},
		filters: {
			toFixed(x) {
				return parseFloat(x).toFixed(1);
			}
		},
		methods: {
			// 获取商品总分类
			getbig() {
				let timestamp = (new Date()).getTime();
				let secret = md5.hex_md5("XM#" + md5.hex_md5(String(timestamp)) + "@secret");
				let data = {
					store: this.store,
					secret: timestamp,
					timestamp: secret
				}
				ajax({
					url: '/Procate/getBig',
					method: 'post',
					data: data,
					success: (res) => {
						this.categoryList = res.data.splice(1, 10);
					}
				})
			},
			getLocation() {
				uni.getLocation({
					success: (res) => {
						this.getCity(res.latitude, res.longitude) //获取当前所在城市						
					},
					fail: (res) => {
						console.log(res);

					}
				})
			},
			// 获取定位城市
			getCity(latitude, longitude) {
				let demo = new QQMapWX({
					key: 'LVHBZ-SOY6W-Z5URP-OC34Q-CLWZH-Y6BCX' // 开发密钥（key）
				});
				demo.reverseGeocoder({
					location: {
						latitude: latitude,
						longitude: longitude
					},
					success: (res) => {
						console.log(res);
						this.city = res.result.ad_info.city;
						let latitude = res.result.location.lat;
						let longitude = res.result.location.lng;
						this.address = res.result.address
						uni.setStorageSync('local', res.result.address);
						// this.getgoods()
						let params = {
							city: this.city,
							from: latitude + ',' + longitude
						}
						this.$xm.post('/Index/getStoreList', params, (res) => {
							this.storeItems = res.data;
							uni.setStorageSync('storeList', res.data)

						})
					},
					fail: function(res) {
						console.log(res)

					},
					complete: function(res) {

					}
				});
			},
			// 获取拼团
			getassembleList() {
				this.$xm.post('/groupbuy/getlist', '', (res) => {
					this.assembleList = res.list.splice(0, 2);
					this.assembleList.forEach((ele) => {
						ele.pro_sn = 'http://img.xmvogue.com/thumb/' + ele.pro_sn + '.jpg?x-oss-process=style/300';
					})
				})
			},
			// 获取砍价
			getBargin() {
				this.$xm.post('/bargain/getlist', "", res => {
					let list = res.list
					if (list) {
						list.forEach((ele) => {
							ele.pro_img = 'http://img.xmvogue.com/thumb/' + ele.pro_sn + '.jpg?x-oss-process=style/300';
						})
					}
					this.bartainlist = list;
				})
			},
			// 跳转到砍价
			toBargain() {
				uni.navigateTo({
					url: '../seckill/bargain/bargain'
				})
			},
			// 获取商品
			getgoods() {
				this.swiperList = []
				let timestamp = (new Date()).getTime();
				let secret = md5.hex_md5("XM#" + md5.hex_md5(String(timestamp)) + "@secret");
				let data = {
					store: this.store,
					secret: timestamp,
					timestamp: secret
				}
				ajax({
					url: '/Index/getInfo',
					method: 'post',
					data: data,
					success: (res) => {
						let result = res.data;
						this.traitList = result.trait;

						this.popup = result.popup;
						this.popHeight = this.popup.height;
						if (uni.getStorageSync('store')) {
							this.modalShow = false;
						} else {
							if (result.popup.img != '') {
								this.modalShow = true;
							} else {
								this.modalShow = false;
							}
						}
						this.getheight = result.banner[0].height + 'rpx';
						this.specialImgHeight = result.special.height + 'rpx';
						this.hint = result.hint
						uni.setNavigationBarTitle({
							title: this.hint
						});
						this.swiperList = result.banner;
						this.swiperList.map(ele => {
							ele.img = result._URL_ + ele.img
						})
						this.actionBanner = result.img;

						result.faddish.forEach((ele) => {
							ele.itemcount = 0;
							ele.pro_sn = 'http://img.xmvogue.com/thumb/' + ele.pro_sn + '.jpg?x-oss-process=style/300';

						})
						this.Promotion = result.faddish;
						this.bargainList = result.bargain;
						this.productList = result.rem;
						this.bargainList.forEach((ele) => {
							ele.itemcount = 0;
							ele.pro_sn = 'http://img.xmvogue.com/thumb/' + ele.pro_sn + '.jpg?x-oss-process=style/300';
						})
						this.productList.forEach((ele) => {
							ele.pro_sn = 'http://img.xmvogue.com/thumb/' + ele.pro_sn + '.jpg?x-oss-process=style/300';
							ele.itemcount = 0;
							if (ele.pro_name.length > 8) {}
						})
						if (result.special.goods) {
							result.special.goods.map((ele) => {
