			// 加入购物车
			joinCart() {
				let data = {
					proid: this.id,
					pronum: 1,
				};
				this.$xm.post('/Cart/add', data, (res) => {
					let result = res.msg;
					if (res.s == 1) {
						this.addShow = true;
						uni.showToast({
							title: result
						});
					} else if (res.s == 0) {
						this.addShow = false;
						uni.showToast({
							title: result,
							icon: 'none'
						});
					}
					this.$xm.post('/Cart', '', res => {
						uni.setStorage({
							key: 'cart',
							data: res.count,
							success: (res) => {
								uni.getStorage({
									key: 'cart',
									success: (res) => {
										let count = res.data;
										if (res.data !== null) {
